CREATE TABLE  "SHIPPING" 
   (	
    "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOT NULL ENABLE, 
	"DESCRIPTION" VARCHAR2(100) NOT NULL ENABLE, 
	
	CONSTRAINT "SHIPPING_PK" PRIMARY KEY ("ID")
   );

CREATE TABLE  "PERSON" 
   (
    "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOT NULL ENABLE, 
	"FIRST_NAME" VARCHAR2(50) NOT NULL ENABLE, 
	"LAST_NAME" VARCHAR2(50) NOT NULL ENABLE, 
	"ADDRESS" VARCHAR2(100) NOT NULL ENABLE, 
	
	CONSTRAINT "PERSON_PK" PRIMARY KEY ("ID")
   );

CREATE TABLE  "CATEGORY" 
   (
    "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOT NULL ENABLE, 
	"NAME" VARCHAR2(50) NOT NULL ENABLE, 
	 
	CONSTRAINT "CATEGORY_PK" PRIMARY KEY ("ID")
   );

CREATE TABLE  "AUCTIONEER" 
   (
    "PERSON_ID" NUMBER NOT NULL ENABLE, 
	"PHONE_NUMBER" VARCHAR2(10) NOT NULL ENABLE, 
	"EMAIL" VARCHAR2(30) NOT NULL ENABLE, 
	
	CONSTRAINT "AUCTIONEER_PK" PRIMARY KEY ("PERSON_ID"),
    CONSTRAINT "AUCTIONEER_FK" FOREIGN KEY ("PERSON_ID")  REFERENCES PERSON("ID") ON DELETE CASCADE
   );

CREATE TABLE  "PAYMENT" 
   (
    "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOT NULL ENABLE, 
	"NAME" VARCHAR2(50) NOT NULL ENABLE, 
	
	CONSTRAINT "PAYMENT_PK" PRIMARY KEY ("ID")
   );
   
CREATE TABLE  "TYPE" 
   (
    "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOT NULL ENABLE, 
	"NAME" VARCHAR2(50) NOT NULL ENABLE, 
	
	CONSTRAINT "TYPE_PK" PRIMARY KEY ("ID")
   );
   
CREATE TABLE  "AUCTION_ITEM" 
   (
    "ID" NUMBER NOT NULL ENABLE,
    "DESCRIPTION" VARCHAR2(100) NOT NULL ENABLE,
    "START_PRICE" NUMBER NOT NULL ENABLE,
    "OPTIMAL_PRICE" NUMBER NOT NULL ENABLE,
    "CATEGORY_ID" NUMBER NOT NULL ENABLE,
    "TYPE_ID" NUMBER NOT NULL ENABLE,
    "PAYMENT_ID" NUMBER NOT NULL ENABLE,
    "WINNER_ID" NUMBER,
    "OWNER_ID" NUMBER NOT NULL ENABLE,
    CONSTRAINT "AUCTION_ITEM_PK" PRIMARY KEY ("ID"),
    CONSTRAINT "AUCTION_ITEM_CATEGORY_FK" FOREIGN KEY ("CATEGORY_ID")  REFERENCES CATEGORY("ID"),
    CONSTRAINT "AUCTION_ITEM_TYPEY_FK" FOREIGN KEY ("TYPE_ID")  REFERENCES TYPE("ID"),
    CONSTRAINT "AUCTION_ITEM_PAYMENT_FK" FOREIGN KEY ("PAYMENT_ID")  REFERENCES PAYMENT("ID"),
    CONSTRAINT "AUCTION_ITEM_WINNER_FK" FOREIGN KEY ("WINNER_ID")  REFERENCES AUCTIONEER("PERSON_ID"),
    CONSTRAINT "AUCTION_ITEM_OWNER_FK" FOREIGN KEY ("OWNER_ID")  REFERENCES PERSON("ID")
   );
   
CREATE TABLE  "AUCTION_ITEM_SHIPPING" 
   (
    "AUCTION_ITEM_ID" NUMBER NOT NULL ENABLE,
    "SHIPPING_ID" NUMBER NOT NULL ENABLE,
    
	CONSTRAINT "AUCTION_ITEM_SHIPPING_PK" PRIMARY KEY ("AUCTION_ITEM_ID", "SHIPPING_ID"),
    CONSTRAINT "AU_IT_SH_ITEM_FK" FOREIGN KEY ("AUCTION_ITEM_ID")  REFERENCES AUCTION_ITEM("ID"),
    CONSTRAINT "AU_IT_SH_SHIPPING_FK" FOREIGN KEY ("SHIPPING_ID")  REFERENCES SHIPPING("ID")
   );
   
 CREATE TABLE  "BID" 
   (
   "AUCTION_ITEM_ID" NUMBER NOT NULL ENABLE,
   "AUCTIONEER_ID" NUMBER NOT NULL ENABLE,
   "VALUE" NUMBER NOT NULL ENABLE,
   
   CONSTRAINT "AUCTION_ITEM_AUCTIONEER_PK" PRIMARY KEY ("AUCTION_ITEM_ID", "AUCTIONEER_ID", "VALUE"),
   CONSTRAINT "AU_IT_AU_ITEM_FK" FOREIGN KEY ("AUCTION_ITEM_ID")  REFERENCES AUCTION_ITEM("ID"),
   CONSTRAINT "AU_IT_AU_AUCTIONEER_FK" FOREIGN KEY ("AUCTIONEER_ID")  REFERENCES AUCTIONEER("PERSON_ID")
   );